SELECT *
FROM (SELECT a.*, rownum rnum
		FROM ( SELECT *
				FROM (SELECT book_num num, REPLACE(REPLACE(book_title,'<','&lt;'),'>','&gt;') title, book_regdate reg_date, 2 category FROM book WHERE mem_num=#{mem_num}
						UNION
						SELECT helper_num num, REPLACE(REPLACE(helper_title,'<','&lt;'),'>','&gt;') title, helper_reg_date reg_date, 3 category FROM helper WHERE mem_num=#{mem_num})
				)a)

WHERE rnum>=#{start} AND rnum<=#{end}

ORDER BY reg_date DESC

SELECT COUNT(*)
FROM 
	(SELECT community_num num, REPLACE(REPLACE(community_title,'<','&lt;'),'>','&gt;') title, community_reg_date reg_date, 2 category, 0 subCategory FROM community WHERE mem_num=#{mem_num}
		UNION
		SELECT community_num num, REPLACE(REPLACE(re_content,'<','&lt;'),'>','&gt;') title, re_date reg_date, 3 category, 0 subCategory FROM community_reply WHERE mem_num=#{mem_num}
		UNION
		SELECT community_num num, REPLACE(REPLACE(community_title,'<','&lt;'),'>','&gt;') title, SYSDATE reg_date, 4 category, 1 subCategory FROM community_fav t JOIN community f USING(community_num) WHERE t.mem_num=#{mem_num}
		UNION
		SELECT helper_num num, REPLACE(REPLACE(helper_title,'<','&lt;'),'>','&gt;') title, SYSDATE reg_date, 4 category, 2 subCategory FROM helper_scrap t JOIN helper f USING(helper_num) WHERE t.mem_num=#{mem_num}
		UNION
		SELECT book_num num, REPLACE(REPLACE(book_title,'<','&lt;'),'>','&gt;') title, SYSDATE reg_date, 4 category, 3 subCategory FROM book_scrap t JOIN book f USING(book_num) WHERE t.mem_num=#{mem_num})

