SELECT COUNT(*) FROM
(SELECT mem_num, book_num num, REPLACE(REPLACE(book_title,'<','&lt;'),'>','&gt;') title, book_thumbnailname photo, book_regdate reg_date, 1 category FROM book UNION SELECT mem_num, helper_num num, REPLACE(REPLACE(helper_title,'<','&lt;'),'>','&gt;') title, helper_filename photo,helper_reg_date reg_date, 2 category FROM helper UNION SELECT mem_num, community_num num, REPLACE(REPLACE(community_title,'<','&lt;'),'>','&gt;') title, community_filename photo, community_reg_date reg_date, 3 category FROM community UNION SELECT mem_num, sc_num num, REPLACE(REPLACE(sc_title,'<','&lt;'),'>','&gt;') title, sc_filename photo,sc_reg_date reg_date, 4 category FROM second)
JOIN member USING (mem_num) WHERE mem_num=#{mem_num}
